def groovyUtils = new com.eviware.soapui.support.GroovyUtils( context )
//Read header response from Test Step
def httpResponseHeaders = context.testCase.testSteps["Login Submit"].testRequest.response.responseHeaders
// Defines wich headers to transfer into Test Case Properties
def portalToken = httpResponseHeaders["Set-Cookie"][1]
def oskauthToken = httpResponseHeaders["Set-Cookie"][2]
//.toString().replaceAll("\\[|\\]","")
def (name1, ptoken) = portalToken.toString().split("=|\\;")
def (name2, otoken) = oskauthToken.toString().split("=|\\;")
//Writes Log for both headers
log.info("Cookies: " + ptoken)
log.info("Cookies: " + otoken)
//Property transfer
groovyUtils.setPropertyValue("Properties", "portal_token", "portal_token=" + ptoken.toString())
groovyUtils.setPropertyValue("Properties", "oskauth_token", "oskauth_token=" + otoken.toString())

